import React, { useRef, useState } from "react";
import axios from "axios";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

const Signup = ({ mode, setMode }) => {
    const emailRef = useRef(null);
    const passwordRef = useRef(null);
    const otpRef = useRef(null);
    const otpSentRef = useRef(false);
    const otpVerifiedRef = useRef(false);
    const [loading, setLoading] = useState(false);  // Using useState for loading state
    const errorRef = useRef("");
    const successMessageRef = useRef("");
    
    const sendOtp = async () => {
        const email = emailRef.current.value;
        if (!email) {
            errorRef.current = "Please enter a valid email.";
            return;
        }

        setLoading(true);
        errorRef.current = "";
        try {
            const response = await axios.post("http://localhost:4000/api/v1/sendOtp", { email });
            if (response.data.success) {
                otpSentRef.current = true;
                successMessageRef.current = "OTP sent to your email.";
            } else {
                errorRef.current = response.data.message || "Failed to send OTP. Please try again.";
            }
        } catch (err) {
            console.error("Error sending OTP:", err);
            errorRef.current = "An error occurred while sending OTP. Please try again.";
        } finally {
            setLoading(false);  // Set loading to false after the request is complete
        }
    };

    const verifyOtp = async () => {
        const otp = otpRef.current.value;
        if (!otp) {
            errorRef.current = "Please enter the OTP.";
            return;
        }

        setLoading(true);
        errorRef.current = "";
        try {
            const response = await axios.post("http://localhost:4000/api/v1/verifyOtp", { email: emailRef.current.value, otp });
            if (response.data.success) {
                otpVerifiedRef.current = true;
                successMessageRef.current = "OTP verified successfully.";
            } else {
                errorRef.current = response.data.message || "Invalid OTP. Please try again.";
            }
        } catch (err) {
            console.error("Error verifying OTP:", err);
            errorRef.current = "An error occurred while verifying OTP. Please try again.";
        } finally {
            setLoading(false);  // Set loading to false after the request is complete
        }
    };

    const handleSignup = async () => {
        const password = passwordRef.current.value;
        if (!password) {
            errorRef.current = "Please enter a password.";
            return;
        }

        setLoading(true);
        errorRef.current = "";
        try {
            const response = await axios.post("http://localhost:4000/api/v1/signup", { email: emailRef.current.value, password });
            if (response.data.success) {
                successMessageRef.current = "Signup successful! You can now log in.";
            } else {
                errorRef.current = response.data.message || "Signup failed. Please try again.";
            }
        } catch (err) {
            console.error("Error during signup:", err);
            errorRef.current = "An error occurred during signup. Please try again.";
        } finally {
            setLoading(false);  // Set loading to false after the request is complete
        }
    };

    return (
        <div>
            <Navbar />
            <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
                <div className="bg-white p-6 rounded-md shadow-md w-full max-w-md">
                    <h2 className="text-2xl font-bold mb-4 text-center">Signup</h2>

                    {/* Display error or success messages */}
                    {errorRef.current && <p className="text-red-500 text-sm mb-4">{errorRef.current}</p>}
                    {successMessageRef.current && <p className="text-green-500 text-sm mb-4">{successMessageRef.current}</p>}

                    {/* Email input */}
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700">Email</label>
                        <input
                            type="email"
                            ref={emailRef}
                            className="w-full p-2 border border-gray-300 rounded-md"
                            disabled={otpSentRef.current}
                            required
                        />
                    </div>

                    {/* Password input */}
                    {!otpSentRef.current && (
                        <div className="mb-4">
                            <label className="block text-sm font-medium text-gray-700">Password</label>
                            <input
                                type="password"
                                ref={passwordRef}
                                className="w-full p-2 border border-gray-300 rounded-md"
                                required
                            />
                        </div>
                    )}

                    {/* Send OTP button */}
                    {!otpSentRef.current && (
                        <button
                            onClick={sendOtp}
                            className="w-full bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600"
                            disabled={loading}
                        >
                            {loading ? "Sending OTP..." : "Send OTP"}
                        </button>
                    )}

                    {/* OTP input */}
                    {otpSentRef.current && !otpVerifiedRef.current && (
                        <div className="mt-4">
                            <label className="block text-sm font-medium text-gray-700">Enter OTP</label>
                            <input
                                type="text"
                                ref={otpRef}
                                className="w-full p-2 border border-gray-300 rounded-md"
                                maxLength={6}
                                required
                            />
                            <button
                                onClick={verifyOtp}
                                className="w-full mt-4 bg-green-500 text-white p-2 rounded-md hover:bg-green-600"
                                disabled={loading}
                            >
                                {loading ? "Verifying OTP..." : "Verify OTP"}
                            </button>
                        </div>
                    )}

                    {/* Signup button */}
                    {otpVerifiedRef.current && (
                        <button
                            onClick={handleSignup}
                            className="w-full mt-4 bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600"
                            disabled={loading}
                        >
                            {loading ? "Signing Up..." : "Sign Up"}
                        </button>
                    )}
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default Signup;
