import React, { useState, useEffect } from 'react';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

// Register the required elements for Chart.js
ChartJS.register(ArcElement, Tooltip, Legend);

const SpendingDistribution = () => {
    const [error, setError] = useState('');
    const [budgets, setBudgets] = useState([]);
    const [chartData, setChartData] = useState(null); // Initialize as null
    const navigate = useNavigate();

    useEffect(() => {
        const fetchBudgets = async () => {
            try {
                let token = localStorage.getItem("token");
                if (!token) {
                    setError("Unauthorized! Please log in again.");
                    navigate("/login");
                    return;
                }

                const response = await axios.get('http://localhost:4000/api/v1/budgets/current-month', {
                    headers: { Authorization: `Bearer ${token}` }
                });

                const budgetData = response.data?.budgets || []; // Ensure budgets exist
                setBudgets(budgetData);


            } catch (error) {
                console.error('Error fetching budgets:', error);
                setError(error.response?.data?.message || "Failed to load budgets.");
            }
        };

        fetchBudgets();
    }, []);

    useEffect(() => {
        try {

            // Prepare data for the Pie chart
            const labels = budgetData.map(budget => budget.name);
            const data = budgetData.map(budget => budget.spentAmount || 0); // Default to 0 if undefined

            setChartData({
                labels: labels,
                datasets: [{
                    data: data,
                    backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0'], // Customize colors
                }],
            });
        }

        catch (err) {
            setChartData(null); // Handle case with no budgets

        }

    },)

    return (
        <div className="chart-container">
            <h3 className="text-xl font-semibold mb-4">Spending Distribution by Budget</h3>
            {error && <p className="text-red-500">{error}</p>}
            {chartData ? (
                <Pie data={chartData} />
            ) : (
                <p className="text-gray-500">No data available for the current month.</p>
            )}
        </div>
    );
};

export default SpendingDistribution;
