import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const GenerateReport = () => {
    const [budget, setBudgets] = useState(""); // Budget for the current month
    const [aiResponse, setAiResponse] = useState(""); // AI-generated report
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");
    const navigate = useNavigate();

    // Default budget (fetching from localStorage or setting default)
    useEffect(() => {
        const fetchbudgets = async () => {
            let token = localStorage.getItem("token");
            if (!token) {
                setError("Unauthorized! Please log in again.");
                navigate("/login");
                return;
            }

            const response = await axios.get('https://finance-tracker-backend-0h5z.onrender.com/api/v1/budgets/current-month', {
                headers: { Authorization: `Bearer ${token}` }
            });

            const budgetData = response.data?.budgets || []; // Ensure budgets exist
            setBudgets(budgetData);
            fetchAiResponse(budgetData); // Fetch report on page load
        }
        fetchbudgets();
        // eslint-disable-next-line
    }, []);

    // Fetch AI response from the backend
    const fetchAiResponse = async (budgetValue) => {
        setLoading(true);
        setError("");

        try {
            const response = await axios.post("http://localhost:4000/api/v1/getairesponse", {
                prompt: `Generate a financial report for the current month with a budget of $${budgetValue}. Include savings, total expenses, and top categories.`,
            });

            setAiResponse(response.data.response); // Update the response
        } catch (err) {
            console.error(err);
            setError("Failed to generate the report. Please try again later.");
        } finally {
            setLoading(false);
        }
    };

    // Handle budget input change
    const handleBudgetChange = (e) => {
        const value = e.target.value;
        setBudgets(value);
        localStorage.setItem("budget", value); // Save budget to localStorage
    };

    // Submit the budget to fetch the report
    const handleGenerateReport = () => {
        fetchAiResponse(budget);
    };

    return (
        <div className="min-h-screen bg-gradient-to-b from-gray-800 to-gray-400 text-white flex flex-col items-center p-6">
            <h1 className="text-4xl font-bold mb-4">Expense Report</h1>
            <p className="text-lg mb-8">Generate your financial report for the current month or based on your budget.</p>

            <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-md text-gray-800">
                <label htmlFor="budget" className="block text-lg font-medium mb-2">
                    Enter Monthly Budget ($)
                </label>
                <input
                    id="budget"
                    type="number"
                    value={budget}
                    onChange={handleBudgetChange}
                    className="w-full p-2 border rounded-lg mb-4"
                />
                <button
                    onClick={handleGenerateReport}
                    className="w-full bg-blue-600 text-white p-3 rounded-lg hover:bg-blue-700 transition"
                >
                    Generate Report
                </button>
            </div>

            {loading ? (
                <p className="mt-6 text-lg">Generating your report...</p>
            ) : error ? (
                <p className="mt-6 text-lg text-red-500">{error}</p>
            ) : (
                aiResponse && (
                    <div className="bg-white rounded-lg shadow-lg p-6 mt-8 w-full max-w-3xl text-gray-800">
                        <h2 className="text-xl font-bold mb-4">Generated Report</h2>
                        <p className="whitespace-pre-wrap">{aiResponse}</p>
                    </div>
                )
            )}
        </div>
    );
};

export default GenerateReport;
