import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import axios from 'axios';

const SpendingDistributionBar = () => {
    const [budgets, setBudgets] = useState([]);
    const [chartData, setChartData] = useState({});

    useEffect(() => {
        const fetchBudgets = async () => {
            try {
                let token = localStorage.getItem("token");
                if (!token) {
                    setError("Unauthorized! Please log in again.");
                    navigate("/login");
                    return;
                }

                const response = await axios.get('http://localhost:4000/api/v1/budgets/current-month', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                
                const budgetData = response.data.budgets;
                setBudgets(budgetData);

                // Prepare data for the Bar chart
                const labels = budgetData.map(budget => budget.name);
                const data = budgetData.map(budget => budget.spentAmount);

                setChartData({
                    labels: labels,
                    datasets: [{
                        label: 'Amount Spent',
                        data: data,
                        backgroundColor: 'rgba(75,192,192,0.6)',
                        borderColor: 'rgba(75,192,192,1)',
                        borderWidth: 1,
                    }],
                });
            } catch (error) {
                console.error('Error fetching budgets:', error);
            }
        };

        fetchBudgets();
    }, []);

    return (
        <div className="chart-container">
            <h3 className="text-xl font-semibold">Spending Distribution by Budget</h3>
            <Bar data={chartData} />
        </div>
    );
};

export default SpendingDistributionBar;
