import React, { useRef } from 'react';
import { jsPDF } from 'jspdf';

const BudgetReport = ({ aiResponse }) => {
  const contentRef = useRef();

  const handleDownloadPDF = () => {
    const doc = new jsPDF();

    // Capture the HTML content inside the contentRef div and convert it into PDF
    doc.html(contentRef.current, {
      callback: (doc) => {
        doc.save('budget_report.pdf'); // Save the generated PDF
      },
      margin: [10, 10, 10, 10], // Adjust margins if needed
      autoPaging: true, // Automatically add page breaks if content overflows
    });
  };

  return (
    <div>
      <h1>AI Generated Budget Report</h1>
      
      {/* The div is referenced by contentRef for PDF generation */}
      <div
        ref={contentRef}
        dangerouslySetInnerHTML={{ __html: aiResponse }}
        style={{ padding: '20px', border: '1px solid #ccc', borderRadius: '8px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)' }}
      />
      
      {/* Button to trigger PDF download */}
      <button
        onClick={handleDownloadPDF}
        style={{ marginTop: '20px', padding: '10px 20px', backgroundColor: '#4CAF50', color: 'white', borderRadius: '4px' }}
      >
        Download as PDF
      </button>
    </div>
  );
};

export default BudgetReport;
