import React, { useState, useEffect } from "react";

const ReCaptchaV2Label = ({ onVerify }) => {
  const [isVerified, setIsVerified] = useState(false);

  useEffect(() => {
    let script;

    // Load the reCAPTCHA v2 script
    if (!window.grecaptcha) {
      script = document.createElement("script");
      script.src = "https://www.google.com/recaptcha/api.js";
      script.async = true;
      script.defer = true;
      document.body.appendChild(script);

      script.onload = () => {
        console.log("reCAPTCHA script loaded");
      };

      return () => {
        // Clean up the script on component unmount
        if (script) {
          document.body.removeChild(script);
        }
      };
    }
  }, []);

  const handleVerify = (event) => {
    const widgetId = window.grecaptcha.render(event.target, {
      sitekey: process.env.REACT_APP_SITE_KEY_RECAPTCHA, // Your v2 site key
      callback: (token) => {
        setIsVerified(true);
        onVerify(token);
      },
      "expired-callback": () => {
        setIsVerified(false);
        console.log("reCAPTCHA expired. Please verify again.");
      },
    });

    // If already verified, re-rendering isn't necessary
    if (isVerified) {
      window.grecaptcha.reset(widgetId);
    }
  };

  return (
    <div className="mb-4">
      <label
        className="recaptcha-label"
        style={{
          display: "flex",
          alignItems: "center",
          cursor: "pointer",
          fontSize: "18px",
        }}
      >
        <div
          className="g-recaptcha"
          data-sitekey={process.env.REACT_APP_SITE_KEY_RECAPTCHA}
          data-callback="handleVerify"
        ></div>
      </label>
    </div>
  );
};

export default ReCaptchaV2Label;
