import { useEffect } from "react";

const ReCaptchaV3 = ({ onVerify }) => {
    useEffect(() => {
        const loadRecaptchaScript = () => {
            const script = document.createElement("script");
            script.src = `https://www.google.com/recaptcha/api.js?render=${process.env.REACT_APP_SITE_KEY_RECAPTCHA}`;
            script.async = true;
            document.body.appendChild(script);
        };

        loadRecaptchaScript();
    }, []);

    const handleVerify = async () => {
        const token = await window.grecaptcha.execute(
            process.env.REACT_APP_SITE_KEY_RECAPTCHA,
            { action: "submit" }
        );
        onVerify(token); // Send the token to the parent component
    };

    return <button onClick={handleVerify}>Verify</button>;
};

export default ReCaptchaV3;
