import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

const GenerateReport = ({ mode, setMode }) => {
    const [budgets, setBudgets] = useState([]);
    const [loading, setLoading] = useState(false);
    const [aiLoading, setAiLoading] = useState(false);
    const [error, setError] = useState("");
    const [aiResponse, setAiResponse] = useState("");
    const navigate = useNavigate();

    const getToken = () => {
        const token = localStorage.getItem("token");
        if (!token) {
            setError("Unauthorized! Please log in again.");
            navigate("/login");
            return null;
        }
        return token;
    };

    useEffect(() => {
        const fetchBudgets = async () => {
            const token = getToken();
            if (!token) return;

            try {
                setLoading(true);
                const response = await axios.get(`http://localhost:4000/api/v1/getBudgetWithExpenses`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setBudgets(response.data?.budgets || []);
            } catch (err) {
                setError(err.response?.data?.message || "Failed to fetch budget data. Please try again later.");
            } finally {
                setLoading(false);
            }
        };
        fetchBudgets();
    }, [navigate]);

    useEffect(() => {
        const fetchAiResponse = async () => {
            if (!budgets.length) return;

            const extractedData = budgets.map((item) => ({
                name: item.name,
                expenses: item.expenses.map((expense) => ({
                    amount: expense.amount,
                    date: expense.date,
                    description: expense.description || "No description",
                })),
            }));

            console.log("Extracted data", extractedData);

            const promptData = {
                prompt: `Create a detailed report based on the expenses for each budget and also suggest few suggestion adn features which can be suggested by a finance trackeing site to the user based on the budgets. The budgets and their corresponding expenses are as follows: ${JSON.stringify(extractedData)}`,
            };

            const token = getToken();
            if (!token) return;

            try {
                setAiLoading(true);
                const response = await axios.post(
                    "http://localhost:4000/api/v1/getairesponse",
                    promptData,
                    { headers: { Authorization: `Bearer ${token}` } }
                );
                setAiResponse(response.data.response);
            } catch (error) {
                setError("Failed to fetch AI response. Please try again later.");
            } finally {
                setAiLoading(false);
            }
        };
        fetchAiResponse();
    }, [budgets]);

    return (
        <div>
            <Navbar mode={mode} setMode={setMode} />
            <div className="min-h-screen bg-gradient-to-b from-gray-800 to-gray-400 text-white flex flex-col items-center p-6">
                <h1 className="text-4xl font-bold mb-4">Expense Report</h1>
                <p className="text-lg mb-8">Generate your financial report for the current month based on your budget.</p>

                {loading ? (
                    <p className="mt-6 text-lg">Loading your budget data...</p>
                ) : error ? (
                    <p className="mt-6 text-lg text-red-500">{error}</p>
                ) : (
                    budget && (
                        <div className="bg-white rounded-lg shadow-lg p-6 mt-8 w-full max-w-3xl text-gray-800">
                            <h2 className="text-xl font-bold mb-4">Generated Budget Report</h2>
                            {aiLoading ? (
                                <p>Generating AI response...</p>
                            ) : aiResponse ? (
                                <div className="whitespace-pre-wrap">
                                    <ReactMarkdown>{aiResponse}</ReactMarkdown>
                                </div>
                            ) : (
                                <p>No AI response generated yet.</p>
                            )}
                        </div>
                    )
                )}
            </div>
            <Footer mode={mode} />
        </div>
    );
};

export default GenerateReport;
