import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import axios from 'axios';

const ExpenseTrends = () => {
    const [expenses, setExpenses] = useState([]);
    const [chartData, setChartData] = useState({});

    useEffect(() => {
        const fetchExpenses = async () => {
            try {
                const response = await axios.get('http://localhost:4000/api/v1/expenses/trends'); // Add your endpoint here
                const expenseData = response.data.expenses;
                setExpenses(expenseData);

                // Prepare data for the Line chart
                const dates = expenseData.map(expense => expense.date);
                const amounts = expenseData.map(expense => expense.amount);

                setChartData({
                    labels: dates,
                    datasets: [{
                        label: 'Expenses Over Time',
                        data: amounts,
                        fill: false,
                        borderColor: '#FF5733', // Customize line color
                        tension: 0.1,
                    }],
                });
            } catch (error) {
                console.error('Error fetching expenses:', error);
            }
        };

        fetchExpenses();
    }, []);

    return (
        <div className="chart-container">
            <h3 className="text-xl font-semibold">Expense Trends Over Time</h3>
            <Line data={chartData} />
        </div>
    );
};

export default ExpenseTrends;
