import { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';

const AddExpenseForm = ({ mode, setMode }) => {
    const [name, setName] = useState('');
    const [amount, setAmount] = useState('');
    const [category, setCategory] = useState('');
    const [date, setDate] = useState('');
    const [message, setMessage] = useState('');
    const [budgets, setBudgets] = useState([]); // To store the list of available budgets

    // Fetch the list of budgets from the database on component mount
    useEffect(() => {
        const fetchBudgets = async () => {
            try {
                const response = await axios.get('http:localhost:4000/api/v1/budgets/current-month'); // Make a call to your backend to fetch budgets
                setBudgets(response.data.budgets); // Store the list of budgets
            } catch (error) {
                console.error('Error fetching budgets:', error);
            }
        };

        fetchBudgets();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Add expense to the backend
            const response = await axios.post('http:localhost:4000/api/v1/addexpense', {
                
                name,
                amount,
                category,
                date,
            });

            // If expense is added successfully, update the budget's total amount spent
            if (response.data.success) {
                const budgetResponse = await axios.put('/api/budgets/update-spent', {
                    budgetId: category, // The selected budget ID from the dropdown
                    amountSpent: amount, // The amount spent for this expense
                });
                setMessage(`Expense added successfully! Budget updated.`);
            } else {
                setMessage(response.data.message);
            }
        } catch (error) {
            setMessage(error.response?.data?.message || 'Error adding expense');
        }
    };

    return (
        <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
            <Navbar mode={mode} setMode={setMode} />
            <div className="max-w-2xl mx-auto bg-white dark:bg-gray-800 p-6 rounded-lg shadow-md mt-8">
                <h3 className="text-2xl font-semibold text-gray-900 dark:text-white mb-6">Add New Expense</h3>
                <form onSubmit={handleSubmit}>
                    <div className="mb-4">
                        <label htmlFor="name" className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                            Expense Name
                        </label>
                        <input
                            type="text"
                            id="name"
                            className="mt-1 block w-full border border-gray-300 dark:border-gray-700 p-3 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 dark:bg-gray-900 dark:text-white"
                            placeholder="Expense Name"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            required
                        />
                    </div>

                    <div className="mb-4">
                        <label htmlFor="amount" className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                            Amount
                        </label>
                        <input
                            type="number"
                            id="amount"
                            className="mt-1 block w-full border border-gray-300 dark:border-gray-700 p-3 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 dark:bg-gray-900 dark:text-white"
                            placeholder="Amount"
                            value={amount}
                            onChange={(e) => setAmount(e.target.value)}
                            required
                        />
                    </div>

                    <div className="mb-4">
                        <label htmlFor="category" className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                            Select Category (Budget)
                        </label>
                        <select
                            id="category"
                            className="mt-1 block w-full border border-gray-300 dark:border-gray-700 p-3 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 dark:bg-gray-900 dark:text-white"
                            value={category}
                            onChange={(e) => setCategory(e.target.value)}
                            required
                        >
                            <option value="">Select Budget</option>
                            {budgets.map((budget) => (
                                <option key={budget._id} value={budget._id}>
                                    {budget.name} - ${budget.amount} Remaining
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className="mb-4">
                        <label htmlFor="date" className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                            Expense Date
                        </label>
                        <input
                            type="date"
                            id="date"
                            className="mt-1 block w-full border border-gray-300 dark:border-gray-700 p-3 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 dark:bg-gray-900 dark:text-white"
                            value={date}
                            onChange={(e) => setDate(e.target.value)}
                            required
                        />
                    </div>

                    <button
                        type="submit"
                        className="w-full py-3 px-4 bg-indigo-600 hover:bg-indigo-700 text-white font-semibold rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                    >
                        Add Expense
                    </button>
                    {message && (
                        <p className="mt-4 text-sm text-center text-red-600 dark:text-red-400">{message}</p>
                    )}
                </form>
            </div>
            <Footer />
        </div>
    );
};

export default AddExpenseForm;
