import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

const GenerateReport = ({ mode, setMode }) => {
    const [budget, setBudgets] = useState(""); // Budget for the current month
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");
    const [aiResponse, setAiResponse] = useState(""); // AI-generated report (no longer used for AI calls)
    const navigate = useNavigate();

    // Default budget (fetching from localStorage or setting default)
    useEffect(() => {
        const fetchBudgets = async () => {
            let token = localStorage.getItem("token");
            if (!token) {
                setError("Unauthorized! Please log in again.");
                navigate("/login");
                return;
            }

            try {
                setLoading(true);
                const response = await axios.get('http://localhost:4000/api/v1/getBudgetWithExpenses', {
                    headers: { Authorization: `Bearer ${token}` }
                });

                const budgetData = response.data?.budgets || []; // Ensure budgets exist
                setBudgets(budgetData);
            } catch (err) {
                console.error(err);
                setError("Failed to fetch budget data. Please try again later.");
            } finally {
                setLoading(false);
            }
        }
        fetchBudgets();
        // eslint-disable-next-line
    }, []);

    useEffect(() => {
        const fetchAiResponse = async () => {
            
            const promptData = {
                prompt: `Proceed with the following budget and expense details: ${JSON.stringify(budget)}`
            };
            console.log(promptData);

            try {
                // Await the API call to get the response
                let token = localStorage.getItem("token");
            if (!token) {
                setError("Unauthorized! Please log in again.");
                navigate("/login");
                return;
            }
                const response = await axios.get(
                    'http://localhost:4000/api/v1/getairesponse',
                    promptData,
                    {
                        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
                    }
                );

                // Set the AI response in state
                setAiResponse(response.data.response);

            } catch (error) {
                // Handle the error, maybe log it or set an error state
                console.error('Error fetching AI response:', error);
            }
        };

        if (budget.length > 0) {  // Ensure budget is not empty
            fetchAiResponse();
        }
    }, [budget])

return (
    <div>
        <Navbar mode={mode} setMode={setMode} />
        <div className="min-h-screen bg-gradient-to-b from-gray-800 to-gray-400 text-white flex flex-col items-center p-6">
            <h1 className="text-4xl font-bold mb-4">Expense Report</h1>
            <p className="text-lg mb-8">Generate your financial report for the current month based on your budget.</p>

            {loading ? (
                <p className="mt-6 text-lg">Loading your budget data...</p>
            ) : error ? (
                <p className="mt-6 text-lg text-red-500">{error}</p>
            ) : (
                budget && (
                    <div className="bg-white rounded-lg shadow-lg p-6 mt-8 w-full max-w-3xl text-gray-800">
                        <h2 className="text-xl font-bold mb-4">Generated Budget Report</h2>
                        <pre className="whitespace-pre-wrap">
                            {JSON.stringify(aiResponse, null, 2)}
                        </pre>
                    </div>
                )
            )}
        </div>
        <Footer mode={mode} />
    </div>
);
};

export default GenerateReport;
