import React, { useEffect, useState } from "react";

const ReCaptchaV3Button = ({ onVerify }) => {
  const [isVerified, setIsVerified] = useState(false);

  useEffect(() => {
    const loadRecaptchaScript = () => {
      const script = document.createElement("script");
      script.src = `https://www.google.com/recaptcha/api.js?render=${process.env.REACT_APP_SITE_KEY_RECAPTCHA}`;
      script.async = true;
      document.body.appendChild(script);
    };

    loadRecaptchaScript();
  }, []);

  const handleVerify = async () => {
    // Get the reCAPTCHA token when the user interacts with the "checkbox"
    const token = await window.grecaptcha.execute(process.env.REACT_APP_SITE_KEY_RECAPTCHA, {
      action: "submit", // Action name can be custom, like "login" or "submit"
    });
    setIsVerified(true); // You can track if the user is verified or not
    onVerify(token); // Pass token to the parent component for backend verification
  };

  return (
    <div>
      <button
        className={`recaptcha-button ${isVerified ? "verified" : ""}`}
        onClick={handleVerify}
      >
        {isVerified ? "Verified!" : "Click 'I'm Not A Robot'"}
      </button>
    </div>
  );
};

export default ReCaptchaV3Button;
