import React, { useState, useEffect } from "react";

const ReCaptchaV3Label = ({ onVerify }) => {
  const [isVerified, setIsVerified] = useState(false);

  useEffect(() => {
    if (!window.grecaptcha) {
      const script = document.createElement("script");
      script.src = `https://www.google.com/recaptcha/api.js?render=${process.env.REACT_APP_SITE_KEY_RECAPTCHA}`;
      script.async = true;
      document.body.appendChild(script);
  
      script.onload = () => {
        console.log("reCAPTCHA script loaded");
      };
  
      return () => {
        // Clean up the script on component unmount
        document.body.removeChild(script);
      };
    }
  }, []);
  

  const handleVerify = async () => {
    const token = await window.grecaptcha.execute(process.env.REACT_APP_SITE_KEY_RECAPTCHA, {
      action: "submit",
    });
    setIsVerified(true);
    onVerify(token);
  };

  return (
    <div>
      <label
        className="recaptcha-label"
        style={{
          display: "flex",
          alignItems: "center",
          cursor: "pointer",
          fontSize: "18px",
        }}
      >
        <input
          type="checkbox"
          onClick={handleVerify}
          checked={isVerified}
          style={{
            marginRight: "10px",
          }}

        />
        <span onClick={handleVerify}>
          {isVerified ? "Verified!" : "I’m not a robot"}
        </span>
      </label>
    </div>
  );
};

export default ReCaptchaV3Label;
