import React from 'react';
import SpendingDistribution from '../components/SpendingDistribution';
import SpendingDistributionBar from '../components/SpendingDistributionBar';
import ExpenseTrends from '../components/ExpenseTrends';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';

const Dashboard = ({ mode, setMode }) => {
    return (
        <div className="min-h-screen bg-gray-100 flex flex-col">
            {/* Navbar */}
            <Navbar mode={mode} setMode={setMode} />

            {/* Dashboard Header */}
            <header className="text-center py-8 bg-blue-600 text-white">
                <h1 className="text-4xl font-bold">Expense Dashboard</h1>
                <p className="text-xl mt-2">Monitor your spending and trends over time</p>
            </header>

            {/* Dashboard Content */}
            <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-12">
                <div className="flex flex-col sm:flex-col sm:flex-wrap justify-center items-center gap-10">

                    {/* Spending Distribution Component */}
                    <div className="w-full  lg:w-1/4 h-auto bg-white shadow-lg rounded-lg p-6 transform hover:scale-105 transition-all duration-300">
                        <h2 className="text-xl font-semibold mb-4 text-center text-gray-800">Spending Distribution</h2>
                        <SpendingDistribution />
                    </div>

                    {/* Spending Distribution Bar Component */}
                    <div className="w-full lg:w-1/4  bg-white shadow-lg rounded-lg p-6 transform hover:scale-105 transition-all duration-300">
                        <h2 className="text-xl font-semibold mb-4 text-center text-gray-800">Spending Distribution by Category</h2>
                        <SpendingDistributionBar />
                    </div>

                    {/* Expense Trends Component */}
                    <div className="w-full sm:w-1/3 lg:w-2/4 h-auto bg-white shadow-lg rounded-lg p-6 transform hover:scale-105 transition-all duration-300">
                        <h2 className="text-xl font-semibold mb-4 text-center text-gray-800">Expense Trends Over Time</h2>
                        <ExpenseTrends />
                    </div>

                </div>
            </div>

            {/* Footer */}
            <Footer />
        </div>
    );
};

export default Dashboard;
