import React from "react";
import Slider from "react-slick"; // Importing react-slick carousel

// Carousel Component
const Carousel = ({ slides }) => {
  // Slick slider settings
  const settings = {
    dots: true, // Show dots for navigation
    infinite: true, // Infinite scroll
    speed: 500, // Transition speed
    slidesToShow: 1, // Show one slide at a time
    slidesToScroll: 1, // Scroll one slide at a time
    autoplay: true, // Auto-play the slides
    autoplaySpeed: 3000, // Speed of auto-play
    pauseOnHover: true, // Pause on hover
  };

  return (
    <div className="w-full h-full mx-auto">
      <Slider {...settings}>
        {slides.map((slide, index) => (
          <div key={index} className="text-center">
            <img
              src={slide.image}
              alt={slide.title}
              className="mx-12 object-cover autofill: rounded-lg"
            />
            <h3 className="text-xl font-semibold mt-4">{slide.title}</h3>
            <p className="text-md mt-2 text-gray-600">{slide.description}</p>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default Carousel;
