import React from 'react';
import SpendingDistribution from '../SpendingDistribution';
import SpendingDistributionBar from '../SpendingDistributionBar';
import ExpenseTrends from '../ExpenseTrends';

const Dashboard = () => {
    return (
        <div className="min-h-screen bg-gray-100 p-6">
            {/* Dashboard Header */}
            <header className="mb-8 text-center">
                <h1 className="text-3xl font-bold text-gray-800">Expense Dashboard</h1>
                <p className="text-lg text-gray-600">Monitor your spending and trends over time</p>
            </header>

            {/* Dashboard Content */}
            <div className="flex flex-col sm:flex-row sm:flex-wrap w-full h-[50vh] mb-12 justify-center items-center gap-10 mt-4">

                {/* Spending Distribution Component */}
                <div className="w-full sm:w-1/3 h-auto bg-white shadow-lg rounded-lg p-6">
                    <h2 className="text-xl font-semibold mb-4 text-center text-gray-800">Spending Distribution</h2>
                    <SpendingDistribution />
                </div>

                {/* Spending Distribution Bar Component */}
                <div className="w-full sm:w-1/3 h-auto bg-white shadow-lg rounded-lg p-6">
                    <h2 className="text-xl font-semibold mb-4 text-center text-gray-800">Spending Distribution by Category</h2>
                    <SpendingDistributionBar />
                </div>

                {/* Expense Trends Component */}
                <div className="w-full sm:w-1/3 h-auto bg-white shadow-lg rounded-lg p-6">
                    <h2 className="text-xl font-semibold mb-4 text-center text-gray-800">Expense Trends Over Time</h2>
                    <ExpenseTrends />
                </div>

            </div>
        </div>
    );
};

export default Dashboard;
