import React, { useState, useEffect } from "react";
import Navbar from "../components/Navbar";
import { FaArrowRight, FaArrowLeft } from "react-icons/fa"; // For carousel navigation

const LandingPage = ({ mode, setMode }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const slides = [
    { title: "Track Income & Expenses", description: "Manage your financial inflows and outflows with ease." },
    { title: "Set Budgets", description: "Create and stick to budgets for better financial control." },
    { title: "Visual Reports", description: "Gain insights with visual reports on spending and savings." },
  ];

  const [showModal, setShowModal] = useState(false);
  const [activeFeature, setActiveFeature] = useState("");

  useEffect(() => {
    const autoSlide = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 4000); // Auto-slide every 4 seconds
    return () => clearInterval(autoSlide);
  }, []);

  const handleNext = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const handlePrev = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  const handleFeatureHover = (feature) => {
    setActiveFeature(feature);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <div className={mode === "dark" ? "bg-gray-700 text-white" : "bg-white text-black"}>
      {/* Navigation */}
      <Navbar mode={mode} setMode={setMode} />

      {/* Hero Section */}
      <section className="flex justify-center items-center h-screen text-center px-5 py-10 relative overflow-hidden">
        <div className="absolute inset-0 bg-cover bg-center opacity-30" style={{ backgroundImage: 'url("https://via.placeholder.com/1500x900")' }}></div>
        <div className="max-w-3xl space-y-6 z-10">
          <h2 className="text-4xl font-semibold text-white animate__animated animate__fadeIn animate__delay-1s">
            Track and Manage Your Finances with Ease
          </h2>
          <p className="text-lg text-white animate__animated animate__fadeIn animate__delay-1.5s">
            Our app helps you track income, expenses, savings, and much more. Take control of your financial future today!
          </p>
          <button
            className="mt-6 px-8 py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition ease-in-out duration-300"
          >
            Get Started
          </button>
        </div>
      </section>

      {/* Carousel Section */}
      <section className="py-10 bg-gray-100">
        <h2 className="text-3xl font-semibold text-center mb-8">Key Features</h2>
        <div className="max-w-3xl mx-auto relative">
          <div className="carousel bg-white p-6 rounded-lg shadow-xl transition transform duration-500 ease-in-out">
            <h3 className="text-2xl font-semibold">{slides[currentSlide].title}</h3>
            <p>{slides[currentSlide].description}</p>
          </div>
          <div className="absolute top-1/2 left-0 transform -translate-y-1/2">
            <button
              onClick={handlePrev}
              className="bg-blue-500 text-white p-2 rounded-full hover:bg-blue-600"
            >
              <FaArrowLeft />
            </button>
          </div>
          <div className="absolute top-1/2 right-0 transform -translate-y-1/2">
            <button
              onClick={handleNext}
              className="bg-blue-500 text-white p-2 rounded-full hover:bg-blue-600"
            >
              <FaArrowRight />
            </button>
          </div>
        </div>
      </section>

      {/* Interactive Image Section */}
      <section className="py-20 px-10 text-center bg-blue-500 text-white relative">
        <h2 className="text-3xl font-semibold mb-8">Our App in Action</h2>
        <div className="relative flex justify-center items-center">
          <div className="animate__animated animate__fadeIn animate__delay-2s">
            <img src="https://via.placeholder.com/150" alt="App in action" className="w-32 h-32 rounded-full transition transform hover:scale-110 duration-500" />
          </div>
          <div className="absolute top-1/4 left-1/4">
            <img src="https://via.placeholder.com/150" alt="App in action" className="w-32 h-32 rounded-full transition transform hover:scale-110 duration-500" />
          </div>
          <div className="absolute top-3/4 right-1/4">
            <img src="https://via.placeholder.com/150" alt="App in action" className="w-32 h-32 rounded-full transition transform hover:scale-110 duration-500" />
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-20 px-10">
        <h2 className="text-3xl font-semibold text-center mb-8">Key Features</h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-10">
          <div
            className="bg-gray-100 p-6 rounded-lg shadow-lg hover:shadow-xl transition duration-300 ease-in-out group relative"
            onMouseEnter={() => handleFeatureHover("Budget Creation")}
          >
            <h3 className="text-2xl font-semibold mb-4">Budget Creation</h3>
            <p className="group-hover:block hidden">Set budgets for different categories and get alerts for overspending.</p>
            <div className="absolute top-0 left-0 w-full h-full bg-black opacity-50 transition-opacity duration-300 ease-in-out group-hover:opacity-30"></div>
          </div>

          <div
            className="bg-gray-100 p-6 rounded-lg shadow-lg hover:shadow-xl transition duration-300 ease-in-out group relative"
            onMouseEnter={() => handleFeatureHover("Expense Categorization")}
          >
            <h3 className="text-2xl font-semibold mb-4">Expense Categorization</h3>
            <p className="group-hover:block hidden">Automatically categorize your expenses and track where your money goes.</p>
            <div className="absolute top-0 left-0 w-full h-full bg-black opacity-50 transition-opacity duration-300 ease-in-out group-hover:opacity-30"></div>
          </div>

          <div
            className="bg-gray-100 p-6 rounded-lg shadow-lg hover:shadow-xl transition duration-300 ease-in-out group relative"
            onMouseEnter={() => handleFeatureHover("Visual Reports")}
          >
            <h3 className="text-2xl font-semibold mb-4">Visual Reports</h3>
            <p className="group-hover:block hidden">Get visual insights into your spending habits with monthly and yearly reports.</p>
            <div className="absolute top-0 left-0 w-full h-full bg-black opacity-50 transition-opacity duration-300 ease-in-out group-hover:opacity-30"></div>
          </div>
        </div>
      </section>

      {/* Modal for Feature Details */}
      {showModal && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white p-8 rounded-lg shadow-lg max-w-lg w-full">
            <h3 className="text-2xl font-semibold">{activeFeature}</h3>
            <p className="mt-4">
              More detailed information about the {activeFeature} feature will be displayed here.
            </p>
            <button
              onClick={handleCloseModal}
              className="mt-6 px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Footer */}
      <footer className="bg-gray-700 text-white py-4 text-center">
        <p>&copy; 2025 Personal Finance Tracker. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default LandingPage;
