import { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import { useNavigate } from 'react-router-dom';

const AddExpenseForm = ({ mode, setMode }) => {
    const [name, setName] = useState('');
    const [error, setError] = useState('');
    const [amount, setAmount] = useState('');
    const [category, setCategory] = useState('');
    const [date, setDate] = useState('');
    const [message, setMessage] = useState('');
    const [budgets, setBudgets] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchBudgets = async () => {
            try {
                let token = localStorage.getItem("token");
                if (!token) {
                    setError("Unauthorized! Please log in again.");
                    navigate("/login");
                    return;
                }
                const response = await axios.get('http://localhost:4000/api/v1/budgets/current-month',
                    { headers: { Authorization: `Bearer ${token}` } }
                );
                setBudgets(response.data.budgets);
            } catch (error) {
                console.error('Error fetching budgets:', error);
            }
        };

        fetchBudgets();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            let token = localStorage.getItem("token");
            if (!token) {
                setError("Unauthorized! Please log in again.");
                navigate("/login");
                return;
            }
            const amountSpentInt = parseInt(amount);
            const response = await axios.post('http://localhost:4000/api/v1/addexpense', {
                budgetId: category,
                amountSpent: amountSpentInt,
                name,
                amount,
                date,
            },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            setMessage(`Expense added successfully! Budget updated.`);
        } catch (error) {
            setMessage(error.response?.data?.message || 'Error adding expense');
        }
    };

    return (
        <div className={`${mode === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'} min-h-screen`}>
            <Navbar mode={mode} setMode={setMode} />
            <div className={`${mode === 'dark' ? 'bg-gray-800' : 'bg-white'} max-w-2xl mx-auto p-6 rounded-lg shadow-md mt-8 mb-10`}>
                <h3 className="text-2xl font-semibold mb-6">Add New Expense</h3>
                <form onSubmit={handleSubmit}>
                    <div className="mb-4">
                        <label htmlFor="name" className="block text-sm font-medium mb-2">
                            Expense Name
                        </label>
                        <input
                            type="text"
                            id="name"
                            className={`${mode === 'dark' ? 'bg-gray-900 text-white' : 'bg-white'} border rounded-md p-3 shadow-sm w-full`}
                            placeholder="Expense Name"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            required
                        />
                    </div>

                    <div className="mb-4">
                        <label htmlFor="amount" className="block text-sm font-medium mb-2">
                            Amount
                        </label>
                        <input
                            type="number"
                            id="amount"
                            className={`${mode === 'dark' ? 'bg-gray-900 text-white' : 'bg-white'} border rounded-md p-3 shadow-sm w-full`}
                            placeholder="Amount"
                            value={amount}
                            onChange={(e) => setAmount(e.target.value)}
                            required
                        />
                    </div>

                    <div className="mb-4">
                        <label htmlFor="category" className="block text-sm font-medium mb-2">
                            Select Category (Budget)
                        </label>
                        <select
                            id="category"
                            className={`${mode === 'dark' ? 'bg-gray-900 text-white' : 'bg-white'} border rounded-md p-3 shadow-sm w-full`}
                            value={category}
                            onChange={(e) => setCategory(e.target.value)}
                            required
                        >
                            <option value="">Select Budget</option>
                            {budgets.map((budget) => (
                                <option key={budget._id} value={budget._id}>
                                    {budget.name} - $ {budget.amount - parseInt(budget.spentAmount)} Remaining
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className="mb-4">
                        <label htmlFor="date" className="block text-sm font-medium mb-2">
                            Expense Date
                        </label>
                        <input
                            type="date"
                            id="date"
                            className={`${mode === 'dark' ? 'bg-gray-900 text-white' : 'bg-white'} border rounded-md p-3 shadow-sm w-full`}
                            value={date}
                            onChange={(e) => setDate(e.target.value)}
                            required
                        />
                    </div>

                    <button
                        type="submit"
                        className="w-full py-3 px-4 bg-blue-500 hover:bg-blue-700 text-white font-semibold rounded-lg focus:ring-2"
                    >
                        Add Expense
                    </button>
                    {message && (
                        <p className="mt-4 text-sm text-center text-red-600 dark:text-red-400">{message}</p>
                    )}
                </form>
            </div>
            <Footer mode={mode} />
        </div>
    );
};

export default AddExpenseForm;
