import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

const Signup = ({ mode, setMode }) => {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [error, setError] = useState("");
    const [otp, setOtp] = useState(""); // State to hold OTP
    const [sentOtp, setSentOtp] = useState(false); // State to track if OTP is sent
    const [otpVerified, setOtpVerified] = useState(false); // State to track if OTP is verified
    const [loading, setLoading] = useState(false); // To show loading state during OTP process
    const navigate = useNavigate();

    // Function to send OTP
    const sendOtp = async () => {
        if (!email) {
            setError("Please enter a valid email.");
            return;
        }
        setLoading(true);
        try {
            const response = await axios.post("http://localhost:4000/api/v1/sendOtp", { email_user:email });

            if (response.data.success) {
                setSentOtp(true);
                setError(""); // Clear any previous error
            } else {
                setError(response.data.message);
            }
        } catch (err) {
            console.error("OTP Sending Error: ", err);
            setError("An error occurred while sending OTP. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    // Function to verify OTP
    const verifyOtp = async () => {
        if (!otp) {
            setError("Please enter the OTP.");
            return;
        }
        setLoading(true);
        try {
            const response = await axios.post("http://localhost:4000/api/v1/verify-otp", {
                email,
                otp,
            });

            if (response.data.success) {
                setOtpVerified(true);
                setError(""); // Clear any previous error
            } else {
                setError(response.data.message);
            }
        } catch (err) {
            console.error("OTP Verification Error: ", err);
            setError("An error occurred while verifying OTP. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    // Function to handle final signup
    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!name || !email || !password || !confirmPassword) {
            setError("All fields are required!");
            return;
        }

        if (password !== confirmPassword) {
            setError("Passwords do not match!");
            return;
        }

        if (!otpVerified) {
            setError("Please verify your OTP before signing up.");
            return;
        }

        try {
            const response = await axios.post("https://finance-tracker-backend-0h5z.onrender.com/api/v1/register", {
                name,
                email,
                password,
            });

            if (response.data.success) {
                navigate("/login");
            } else {
                setError(response.data.message);
            }
        } catch (err) {
            console.error("Signup Error: ", err);
            setError("An error occurred. Please try again later.");
        }
    };

    return (
        <div className="flex flex-col min-h-screen bg-gray-100">
            <Navbar mode={mode} setMode={setMode} />
            <div className="flex-grow min-w-[40vw] mb-4 mx-auto mt-10 p-5 border rounded-lg shadow-lg bg-white">
                <h2 className="text-2xl font-bold text-center mb-4">Sign Up</h2>

                <form onSubmit={handleSubmit}>
                    {/* Name Field */}
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700">Name</label>
                        <input
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            className="w-full p-2 border border-gray-300 rounded-md"
                            required
                        />
                    </div>

                    {/* Email Field */}
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700">Email</label>
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            className="w-full p-2 border border-gray-300 rounded-md"
                            required
                        />
                    </div>

                    {/* Send OTP Button */}
                    {!sentOtp ? (
                        <button
                            type="button"
                            onClick={sendOtp}
                            className="w-full bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600"
                            disabled={loading}
                        >
                            {loading ? "Sending OTP..." : "Send OTP"}
                        </button>
                    ) : (
                        <div className="mt-4">
                            {/* OTP Input */}
                            <label className="block text-sm font-medium text-gray-700">Enter OTP</label>
                            <input
                                type="text"
                                value={otp}
                                onChange={(e) => setOtp(e.target.value)}
                                className="w-full p-2 border border-gray-300 rounded-md"
                                required
                            />
                            {/* Verify OTP Button */}
                            <button
                                type="button"
                                onClick={verifyOtp}
                                className="w-full mt-4 bg-green-500 text-white p-2 rounded-md hover:bg-green-600"
                                disabled={loading}
                            >
                                {loading ? "Verifying OTP..." : "Verify OTP"}
                            </button>
                        </div>
                    )}

                    {/* Password Fields */}
                    {otpVerified && (
                        <>
                            <div className="mb-4 mt-4">
                                <label className="block text-sm font-medium text-gray-700">Password</label>
                                <input
                                    type="password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    className="w-full p-2 border border-gray-300 rounded-md"
                                    required
                                />
                            </div>

                            <div className="mb-6">
                                <label className="block text-sm font-medium text-gray-700">Confirm Password</label>
                                <input
                                    type="password"
                                    value={confirmPassword}
                                    onChange={(e) => setConfirmPassword(e.target.value)}
                                    className="w-full p-2 border border-gray-300 rounded-md"
                                    required
                                />
                            </div>

                            <button
                                type="submit"
                                className="w-full bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600"
                            >
                                Sign Up
                            </button>
                        </>
                    )}

                    {error && <p className="text-red-500 text-xs mb-4">{error}</p>}
                </form>

                <div className="mt-4 text-center">
                    <p className="text-sm">
                        Already have an account?{" "}
                        <a href="/login" className="text-blue-500 hover:text-blue-700">
                            Login
                        </a>
                    </p>
                </div>
            </div>
            <Footer mode={mode} />
        </div>
    );
};

export default Signup;
