import React, { useEffect } from "react";

const ReCaptcha = ({ onVerify }) => {
    useEffect(() => {
        // Load Google reCAPTCHA script
        const script = document.createElement("script");
        script.src = `https://www.google.com/recaptcha/api.js?render=${process.env.REACT_APP_SITE_KEY_RECAPTCHA}`;
        script.async = true;
        document.body.appendChild(script);

        return () => {
            document.body.removeChild(script);
        };
    }, []);

    const executeReCaptcha = async () => {
        if (window.grecaptcha) {
            const token = await window.grecaptcha.execute("your_site_key", { action: "login" });
            onVerify(token); // Pass the token back to the parent
        }
    };

    return (
        <div
            className="g-recaptcha"
            data-sitekey="your_site_key"
            data-callback={executeReCaptcha}
        >
            {/* Add a button to manually trigger reCAPTCHA */}
        </div>
    );
};

export default ReCaptcha;
