import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

const BudgetView = ({ mode, setMode }) => {
    const [budgets, setBudgets] = useState([]); // To store fetched budgets
    const [startDate, setStartDate] = useState(""); // Custom start date
    const [endDate, setEndDate] = useState(""); // Custom end date
    const [error, setError] = useState(""); // Error message
    const [loading, setLoading] = useState(false); // Loading indicator

    // Function to fetch budgets for the current month
    const fetchCurrentMonthBudgets = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem("token");
            if (!token) {
                setError("Unauthorized! Please log in again.");
                return;
            }

            const response = await axios.get(
                "http://localhost:4000/api/v1/budgets/current-month",
                {
                    headers: { Authorization: `Bearer ${token}` },
                }
            );

            if (response.data.success) {
                setBudgets(response.data.budgets);
            } else {
                setError(response.data.message);
            }
        } catch (err) {
            setError(
                err.response?.data?.message || "An error occurred while fetching budgets."
            );
        } finally {
            setLoading(false);
        }
    };

    // Function to fetch budgets for a custom date range
    const fetchCustomBudgets = async (e) => {
        e.preventDefault();

        if (!startDate || !endDate) {
            setError("Both start date and end date are required.");
            return;
        }

        if (new Date(startDate) > new Date(endDate)) {
            setError("Start date cannot be later than the end date.");
            return;
        }

        try {
            setLoading(true);
            const token = localStorage.getItem("token");
            if (!token) {
                setError("Unauthorized! Please log in again.");
                return;
            }

            const response = await axios.get(
                `http://localhost:4000/api/v1/budgets/range?startDate=${startDate}&endDate=${endDate}`,
                {
                    headers: { Authorization: `Bearer ${token}` },
                }
            );

            if (response.data.success) {
                setBudgets(response.data.budgets);
            } else {
                setError(response.data.message);
            }
        } catch (err) {
            setError(
                err.response?.data?.message || "An error occurred while fetching budgets."
            );
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        // Fetch budgets for the current month on component mount
        fetchCurrentMonthBudgets();
    }, []);

    return (
        <div className={`min-h-screen ${mode === "dark" ? "bg-gray-900 text-white" : "bg-gray-100 text-gray-800"}`}>
            <Navbar mode={mode} setMode={setMode} />
            <div className="mx-auto px-4 py-8">
                <h2 className="text-2xl font-semibold text-center mb-4">
                    Budget Viewer
                </h2>
                {error && <p className="text-red-500 text-center">{error}</p>}
                {loading && <p className="text-center">Loading...</p>}

                {/* Form for custom date range */}
                <form onSubmit={fetchCustomBudgets} className="mb-6">
                    <div className="flex flex-col md:flex-row justify-center gap-4">
                        <div>
                            <label className="block mb-1 font-medium">Start Date</label>
                            <input
                                type="date"
                                value={startDate}
                                onChange={(e) => setStartDate(e.target.value)}
                                className="px-4 py-2 border rounded-lg w-full"
                            />
                        </div>
                        <div>
                            <label className="block mb-1 font-medium">End Date</label>
                            <input
                                type="date"
                                value={endDate}
                                onChange={(e) => setEndDate(e.target.value)}
                                className="px-4 py-2 border rounded-lg w-full"
                            />
                        </div>
                        <button
                            type="submit"
                            className="px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
                        >
                            Get Budgets
                        </button>
                    </div>
                </form>

                {/* Budget List */}
                <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
                    <h3 className="text-lg font-semibold mb-4">Budgets</h3>
                    {budgets.length > 0 ? (
                        <ul className="space-y-4">
                            {budgets.map((budget) => (
                                <li
                                    key={budget._id}
                                    className="border-b pb-2 dark:border-gray-700"
                                >
                                    <h4 className="font-semibold">{budget.name}</h4>
                                    <p>Total Amount: ${budget.amount}</p>
                                    <p>
                                        Date Range: {budget.startDate} to {budget.endDate}
                                    </p>
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p>No budgets found for the selected period.</p>
                    )}
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default BudgetView;
