import React, { useState } from 'react';
import axios from 'axios';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';

const BudgetForm = ({ mode, setMode }) => {
    const [name, setName] = useState('');
    const [amount, setAmount] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
    
        // Validation before submission
        if (!name || !amount || !startDate || !endDate) {
            setError("All fields are required!");
            return;
        }
        if (new Date(startDate) > new Date(endDate)) {
            setError("Start date cannot be later than the end date.");
            return;
        }
    
        try {
            // Retrieve token from localStorage
            let token = localStorage.getItem("token");
            if (!token) {
                setError("Unauthorized! Please log in again.");
                navigate("/login");
                return;
            }
    
            // Send POST request to create budget
            const response = await axios.post(
                'http://localhost:4000/api/budgets/create',
                { name, amount, startDate, endDate },
                { headers: { Authorization: `Bearer ${token}` } }
            );
    
            // Handle response
            if (response.data.success) {
                // Success feedback and reset form
                alert('Budget created successfully!');
                setName('');
                setAmount('');
                setStartDate('');
                setEndDate('');
            } else {
                setError(response.data.message);
            }
        } catch (err) {
            // Improved error handling
            if (err.response) {
                setError(err.response.data.message || 'An error occurred.');
            } else if (err.request) {
                setError('Network error. Please try again later.');
            } else {
                setError('Something went wrong.');
            }
        }
    };    

    return (
        <div className={`min-h-screen flex flex-col ${mode === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-800'}`}>
            <Navbar mode={mode} setMode={setMode} />
            <main className="flex-grow container mx-auto px-4 py-8">
                <div className="max-w-2xl mx-auto bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6">
                    <h2 className="text-2xl font-semibold mb-4 text-center">Create a Budget</h2>
                    {error && <p className="text-red-500 text-center mb-4">{error}</p>}
                    <form onSubmit={handleSubmit} className="space-y-6">
                        <div>
                            <label className="block mb-1 font-medium">Budget Name</label>
                            <input
                                type="text"
                                placeholder="Budget Name"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                                required
                                className="w-full px-4 py-2 border border-gray-300 rounded-lg dark:bg-gray-700 dark:border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>
                        <div>
                            <label className="block mb-1 font-medium">Total Amount</label>
                            <input
                                type="number"
                                placeholder="Total Amount"
                                value={amount}
                                onChange={(e) => setAmount(e.target.value)}
                                required
                                className="w-full px-4 py-2 border border-gray-300 rounded-lg dark:bg-gray-700 dark:border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>
                        <div>
                            <label className="block mb-1 font-medium">Start Date</label>
                            <input
                                type="date"
                                value={startDate}
                                onChange={(e) => setStartDate(e.target.value)}
                                required
                                className="w-full px-4 py-2 border border-gray-300 rounded-lg dark:bg-gray-700 dark:border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>
                        <div>
                            <label className="block mb-1 font-medium">End Date</label>
                            <input
                                type="date"
                                value={endDate}
                                onChange={(e) => setEndDate(e.target.value)}
                                required
                                className="w-full px-4 py-2 border border-gray-300 rounded-lg dark:bg-gray-700 dark:border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>
                        <button
                            type="submit"
                            className="w-full py-2 px-4 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                        >
                            Create Budget
                        </button>
                    </form>
                </div>
            </main>
            <Footer />
        </div>
    );
};

export default BudgetForm;
