import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import axios from 'axios';
impirt

const ExpenseTrends = () => {
    const [expenses, setExpenses] = useState([]);
    const [chartData, setChartData] = useState({});
    const [startDate, setStartDate] = useState(''); // State for start date
    const [endDate, setEndDate] = useState('');   // State for end date

    const getCurrentMonthDates = () => {
        const now = new Date();
        const firstDay = new Date(now.getFullYear(), now.getMonth(), 1).toISOString().split('T')[0];
        const lastDay = new Date(now.getFullYear(), now.getMonth() + 1, 0).toISOString().split('T')[0];
        return { firstDay, lastDay };
    };

    useEffect(() => {
        const fetchExpenses = async () => {
            try {
                const { firstDay, lastDay } = getCurrentMonthDates(); // Get default dates
                let token = localStorage.getItem("token");
                if (!token) {
                    setError("Unauthorized! Please log in again.");
                    navigate("/login");
                    return;
                }
                const response = await axios.get('http://localhost:4000/api/v1/getexpense', {
                    params: { startDate: firstDay, endDate: lastDay },
                    headers: { Authorization: `Bearer ${token}` }
                });
                const expenseData = response.data.expenses;
                setExpenses(expenseData);

                const dates = expenseData.map(expense => new Date(expense.date).toLocaleDateString());
                const amounts = expenseData.map(expense => expense.amount);

                setChartData({
                    labels: dates,
                    datasets: [{
                        label: 'Expenses Over Time',
                        data: amounts,
                        fill: false,
                        borderColor: '#FF5733',
                        tension: 0.1,
                    }],
                });

            } catch (error) {
                console.error('Error fetching expenses:', error);
            }
        };

        fetchExpenses();
    }, []);

    // Function to handle form submission for filtering
    const handleFilter = async (e) => {
        e.preventDefault(); // Prevent page refresh
        if (!startDate || !endDate) {
            alert('Please select both start and end dates.');
            return;
        }

        try {
            const response = await axios.get('http://localhost:4000/api/v1/expenses/trends', {
                params: { startDate, endDate }, // Pass user-selected date range
            });
            const expenseData = response.data.expenses;
            setExpenses(expenseData);

            // Prepare data for the Line chart
            const dates = expenseData.map(expense => new Date(expense.date).toLocaleDateString());
            const amounts = expenseData.map(expense => expense.amount);

            setChartData({
                labels: dates,
                datasets: [{
                    label: `Expenses from ${startDate} to ${endDate}`,
                    data: amounts,
                    fill: false,
                    borderColor: '#36A2EB',
                    tension: 0.1,
                }],
            });
        } catch (error) {
            console.error('Error fetching filtered expenses:', error);
        }
    };

    return (
        <div className="chart-container">
            <h3 className="text-xl font-semibold mb-4">Expense Trends Over Time</h3>

            {/* Filter Form */}
            <form className="mb-6" onSubmit={handleFilter}>
                <div className="flex items-center gap-4">
                    <label>
                        Start Date:
                        <input
                            type="date"
                            value={startDate}
                            onChange={(e) => setStartDate(e.target.value)}
                            className="border p-2 rounded ml-2"
                        />
                    </label>
                    <label>
                        End Date:
                        <input
                            type="date"
                            value={endDate}
                            onChange={(e) => setEndDate(e.target.value)}
                            className="border p-2 rounded ml-2"
                        />
                    </label>
                    <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
                        Filter
                    </button>
                </div>
            </form>

            {/* Line Chart */}
            {chartData.labels && chartData.labels.length > 0 ? (
                <Line data={chartData} />
            ) : (
                <p>No expense data available for the selected period.</p>
            )}
        </div>
    );
};

export default ExpenseTrends;
