import React, { useState } from "react";
import axios from "axios";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import { useNavigate } from "react-router-dom";
import Hyperspeed from "../components/Hyperspeed";
// import { hyperspeedPresets } from "./HyperspeedPresets"; // Import the presets

const Signup = ({ mode, setMode }) => {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [otp, setOtp] = useState("");
    const [otpSent, setOtpSent] = useState(false);
    const [otpVerified, setOtpVerified] = useState(false);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");
    const [successMessage, setSuccessMessage] = useState("");
    const navigate = useNavigate();

    const sendOtp = async () => {
        if (!email) {
            setError("Please enter a valid email.");
            return;
        }
        setLoading(true);
        setError("");
        setSuccessMessage("");

        try {
            const response = await axios.post("https://finance-tracker-backend-0h5z.onrender.com/api/v1/sendOtp", { email });
            if (response.data.success) {
                setOtpSent(true);
                setSuccessMessage("OTP sent to your email.");
            } else {
                setError(response.data.message || "Failed to send OTP. Please try again.");
            }
        } catch (err) {
            setError("An error occurred while sending OTP. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    const verifyOtp = async () => {
        if (!otp) {
            setError("Please enter the OTP.");
            return;
        }
        setLoading(true);
        setError("");
        setSuccessMessage("");

        try {
            const response = await axios.post("https://finance-tracker-backend-0h5z.onrender.com/api/v1/verifyOtp", { email, otp });
            if (response.data.success) {
                setOtpVerified(true);
                setSuccessMessage("OTP verified successfully.");
            } else {
                setError(response.data.message || "Invalid OTP. Please try again.");
            }
        } catch (err) {
            setError("An error occurred while verifying OTP. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    const handleSignup = async () => {
        if (!name) {
            setError("Please enter your name.");
            return;
        }
        if (!password) {
            setError("Please enter a password.");
            return;
        }
        setLoading(true);
        setError("");
        setSuccessMessage("");

        try {
            const response = await axios.post("https://finance-tracker-backend-0h5z.onrender.com/api/v1/register", { name, email, password });
            if (response.data.success) {
                setSuccessMessage("Signup successful! Redirecting...");
                setTimeout(() => navigate("/login"), 2000);
            } else {
                setError(response.data.message || "Signup failed. Please try again.");
            }
        } catch (err) {
            setError("An error occurred during signup. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="bg-black">
            <Navbar mode={mode} setMode={setMode} />
            {/* <Hyperspeed effectOptions={hyperspeedPresets.cyberpunk} /> Add Hyperspeed with cyberpunk preset */}
            <Hyperspeed 
  effectOptions={{
    onSpeedUp: () => { 
      console.log('Speeding up!');
    },
    onSlowDown: () => { 
      console.log('Slowing down!');
    },
    distortion: 'turbulentDistortion',
    length: 400,
    roadWidth: 10,
    islandWidth: 2,
    lanesPerRoad: 4,
    fov: 90,
    fovSpeedUp: 150,
    speedUp: 2,
    carLightsFade: 0.4,
    totalSideLightSticks: 20,
    lightPairsPerRoadWay: 40,
    shoulderLinesWidthPercentage: 0.05,
    brokenLinesWidthPercentage: 0.1,
    brokenLinesLengthPercentage: 0.5,
    lightStickWidth: [0.12, 0.5],
    lightStickHeight: [1.3, 1.7],
    movingAwaySpeed: [60, 80],
    movingCloserSpeed: [-120, -160],
    carLightsLength: [400 * 0.03, 400 * 0.2],
    carLightsRadius: [0.05, 0.14],
    carWidthPercentage: [0.3, 0.5],
    carShiftX: [-0.8, 0.8],
    carFloorSeparation: [0, 5],
    colors: {
      roadColor: 0x080808,
      islandColor: 0x0a0a0a,
      background: 0x000000,
      shoulderLines: 0xFFFFFF,
      brokenLines: 0xFFFFFF,
      leftCars: [0xD856BF, 0x6750A2, 0xC247AC],
      rightCars: [0x03B3C3, 0x0E5EA5, 0x324555],
      sticks: 0x03B3C3,
    }
  }}
/>

            <div className="flex flex-col items-center justify-center min-h-screen bg-black">
                <div className="bg-white p-8 rounded-lg shadow-xl w-full max-w-md transition-all transform hover:scale-105">
                    <h2 className="text-3xl font-semibold text-center text-indigo-600">Create Your Account</h2>

                    {/* Display error or success messages */}
                    {error && (
                        <p className="text-red-600 text-sm mt-4 bg-red-100 p-2 rounded-md border border-red-200">
                            {error}
                        </p>
                    )}
                    {successMessage && (
                        <p className="text-green-600 text-sm mt-4 bg-green-100 p-2 rounded-md border border-green-200">
                            {successMessage}
                        </p>
                    )}

                    {/* Name input */}
                    <div className="mt-6">
                        <label className="block text-sm font-medium text-gray-700 mb-2">Name</label>
                        <input
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-400 focus:outline-none"
                            placeholder="Enter your full name"
                            disabled={otpSent}
                        />
                    </div>

                    {/* Email input */}
                    <div className="mt-4">
                        <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-400 focus:outline-none"
                            placeholder="Enter your email address"
                            disabled={otpSent}
                        />
                    </div>

                    {/* Password input */}
                    {!otpSent && (
                        <div className="mt-4">
                            <label className="block text-sm font-medium text-gray-700 mb-2">Password</label>
                            <input
                                type="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-400 focus:outline-none"
                                placeholder="Create a password"
                            />
                        </div>
                    )}

                    {/* Send OTP button */}
                    {!otpSent && (
                        <button
                            onClick={sendOtp}
                            className="w-full mt-6 bg-indigo-500 text-white py-2 rounded-lg hover:bg-indigo-600 focus:outline-none focus:ring-2 focus:ring-indigo-400"
                            disabled={loading}
                        >
                            {loading ? "Sending OTP..." : "Send OTP"}
                        </button>
                    )}

                    {/* OTP input */}
                    {otpSent && !otpVerified && (
                        <div className="mt-6">
                            <label className="block text-sm font-medium text-gray-700 mb-2">Enter OTP</label>
                            <input
                                type="text"
                                value={otp}
                                onChange={(e) => setOtp(e.target.value)}
                                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-400 focus:outline-none"
                                maxLength={6}
                                placeholder="6-digit OTP"
                            />
                            <button
                                onClick={verifyOtp}
                                className="w-full mt-4 bg-green-500 text-white py-2 rounded-lg hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-400"
                                disabled={loading}
                            >
                                {loading ? "Verifying OTP..." : "Verify OTP"}
                            </button>
                        </div>
                    )}

                    {/* Signup button */}
                    {otpVerified && (
                        <button
                            onClick={handleSignup}
                            className="w-full mt-6 bg-indigo-500 text-white py-2 rounded-lg hover:bg-indigo-600 focus:outline-none focus:ring-2 focus:ring-indigo-400"
                            disabled={loading}
                        >
                            {loading ? "Signing Up..." : "Sign Up"}
                        </button>
                    )}
                </div>
            </div>
            <Footer mode={mode} />
        </div>
    );
};

export default Signup;
