import React, { useState, useEffect } from 'react';
import SpendingDistribution from '../components/SpendingDistribution';
import SpendingDistributionBar from '../components/SpendingDistributionBar';
import ExpenseTrends from '../components/ExpenseTrends';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';

const Dashboard = ({ mode, setMode }) => {
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Simulate data fetching delay
        setTimeout(() => setLoading(false), 1500);
    }, []);

    if (loading) {
        return (
            <div className={`min-h-screen flex justify-center items-center ${mode === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-800'}`}>
                <div className="text-2xl font-semibold animate-pulse">Loading Dashboard...</div>
            </div>
        );
    }

    return (
        <div className={`min-h-screen flex flex-col ${mode === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-800'}`}>
            {/* Navbar */}
            <Navbar mode={mode} setMode={setMode} />

            {/* Header Section */}
            <header className={`text-center py-8 ${mode === 'dark' ? 'bg-gray-800' : 'bg-blue-500 text-white'}`}>
                <h1 className="text-4xl font-bold tracking-wide">Expense Dashboard</h1>
                <p className="text-lg mt-2 font-light">Monitor and analyze your spending trends effectively</p>
            </header>

            {/* Main Content */}
            <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-10 space-y-10">
                {/* Top Section - Flexbox Cards */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                    {/* Spending Distribution Card */}
                    <div
                        className={`p-6 rounded-xl shadow-lg transform hover:scale-105 transition-transform duration-300 ${
                            mode === 'dark' ? 'bg-gray-800 text-white' : 'bg-white text-gray-800'
                        }`}
                    >
                        <h2 className="text-2xl font-semibold text-center mb-4">Spending Distribution</h2>
                        <div className="flex justify-center items-center h-64">
                            <SpendingDistribution />
                        </div>
                    </div>

                    {/* Spending Distribution Bar Card */}
                    <div
                        className={`p-6 rounded-xl shadow-lg transform hover:scale-105 transition-transform duration-300 ${
                            mode === 'dark' ? 'bg-gray-800 text-white' : 'bg-white text-gray-800'
                        }`}
                    >
                        <h2 className="text-2xl font-semibold text-center mb-4">Spending by Category</h2>
                        <div className="flex justify-center items-center h-64">
                            <SpendingDistributionBar />
                        </div>
                    </div>
                </div>

                {/* Expense Trends Card */}
                <div
                    className={`p-6 rounded-xl shadow-lg transform hover:scale-105 transition-transform duration-300 ${
                        mode === 'dark' ? 'bg-gray-800 text-white' : 'bg-white text-gray-800'
                    }`}
                >
                    <h2 className="text-2xl font-semibold text-center mb-4">Expense Trends Over Time</h2>
                    <div className="h-64">
                        <ExpenseTrends />
                    </div>
                </div>
            </div>

            {/* Footer */}
            <Footer mode={mode} />
        </div>
    );
};

export default Dashboard;
