import React, { useState, useEffect } from "react";

const ReCaptchaV3Label = ({ onVerify }) => {
  const [isVerified, setIsVerified] = useState(false);

 

  const handleVerify = async () => {
    const token = await window.grecaptcha.execute(process.env.REACT_APP_SITE_KEY_RECAPTCHA, {
      action: "submit",
    });
    setIsVerified(true);
    onVerify(token);
  };

  return (
    <div>
      <label
        className="recaptcha-label"
        style={{
          display: "flex",
          alignItems: "center",
          cursor: "pointer",
          fontSize: "18px",
        }}
      >
        <input
          type="checkbox"
          onClick={handleVerify}
          checked={isVerified}
          style={{
            marginRight: "10px",
          }}

        />
        <span onClick={handleVerify}>
          {isVerified ? "Verified!" : "I’m not a robot"}
        </span>
      </label>
    </div>
  );
};

export default ReCaptchaV3Label;
