import React, { useState, useEffect } from 'react';
import { Pie } from 'react-chartjs-2';
import axios from 'axios';


const SpendingDistribution = () => {
    const [error,setError] = useState('');
    const [budgets, setBudgets] = useState([]);
    const [chartData, setChartData] = useState({});

    useEffect(() => {
        const fetchBudgets = async () => {
            try {
                let token = localStorage.getItem("token");
                if (!token) {
                    setError("Unauthorized! Please log in again.");
                    navigate("/login");
                    return;
                }
                const response = await axios.get('http://localhost:4000/api/v1/budgets/current-month',
                    { headers: { Authorization: `Bearer ${token}` } }
                );
                
                const budgetData = response.data.budgets;
                setBudgets(budgetData);

                // Prepare data for the Pie chart
                const labels = budgetData.map(budget => budget.name);
                const data = budgetData.map(budget => budget.spentAmount); // Assume 'spentAmount' is the amount spent

                setChartData({
                    labels: labels,
                    datasets: [{
                        data: data,
                        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0'], // Customize colors
                    }],
                });
            } catch (error) {
                console.error('Error fetching budgets:', error);
            }
        };

        fetchBudgets();
    }, []);

    return (
        <div className="chart-container">
            <h3 className="text-xl font-semibold">Spending Distribution by Budget</h3>
            <Pie data={chartData} />
        </div>
    );
};

export default SpendingDistribution;
