import React, { useState, useEffect } from "react";

const ReCaptchaV3Label = ({ onVerify }) => {
  const [isVerified, setIsVerified] = useState(false);

  useEffect(() => {
    const loadRecaptchaScript = () => {
      const script = document.createElement("script");
      script.src = `https://www.google.com/recaptcha/api.js?render=${process.env.REACT_APP_SITE_KEY_RECAPTCHA}`;
      script.async = true;
      document.body.appendChild(script);
    };

    loadRecaptchaScript();
  }, []);

  const handleVerify = async () => {
    if (window.grecaptcha) {
      const token = await window.grecaptcha.execute(
        process.env.REACT_APP_SITE_KEY_RECAPTCHA,
        {
          action: "submit",
        }
      );
      setIsVerified(true);
      onVerify(token);
    } else {
      console.error("reCAPTCHA is not loaded properly.");
    }
  };

  return (
    <div>
      <label
        className="recaptcha-label"
        style={{
          display: "flex",
          alignItems: "center",
          cursor: "pointer",
          fontSize: "18px",
        }}
      >
        <input
          type="checkbox"
          disabled
          checked={isVerified}
          style={{
            marginRight: "10px",
            pointerEvents: "none", // Prevent interaction directly with the checkbox
          }}
        />
        <button
          type="button"
          onClick={handleVerify}
          style={{
            padding: "10px 20px",
            fontSize: "16px",
            backgroundColor: isVerified ? "#28a745" : "#007bff",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          {isVerified ? "Verified!" : "I’m not a robot"}
        </button>
      </label>
    </div>
  );
};

export default ReCaptchaV3Label;
